// Function to find nth Fibonacci number using dynamic programming
int Fibonacci(int n)
{
    int f[n+2]; // array to store Fibonacci numbers
    int i;

    // base cases
    f[0] = 0;
    f[1] = 1;

    // bottom-up approach
    for (i = 2; i <= n; i++)
    {
        f[i] = f[i-1] + f[i-2];
    }

    return f[n];
}

