//Function to find common elements in two arrays
function findCommonElements(arr1, arr2) {
  //Declare an empty array to store the common elements
  let commonElements = [];

  //Loop through the first array
  for (let i = 0; i < arr1.length; i++) {
    //Loop through the second array
    for (let j = 0; j < arr2.length; j++) {
      //If the elements in both arrays are equal, add it to the commonElements array
      if (arr1[i] === arr2[j]) {
        commonElements.push(arr1[i]);
      }
    }
  }

  //Return the commonElements array
  return commonElements;
}

